import styled from "styled-components";

export const HeaderBox = styled.div`
    display: flex;
    width: 100%;
    height: 5vw;
    display: flex;
    align-items: center;
    gap: 50px;
    margin-left: 20px;
    margin-top: 10px;
`

export const Logo = styled.img`
    width: 14vw;
    height: 10vh;
    background-size: contain;
    background-repeat: no-repeat;
    :root {
  --font-size-lg: 2rem;
  --font-size-md: 1.5rem;
  --font-size-sm: 1rem;
}

@media (min-width: 480px) {
  :root {
    --font-size-lg: 2.75rem;
    --font-size-md: 2rem;
    --font-size-sm: 1.25rem;
  }
}

@media (min-width: 1024px) {
  :root {
    --font-size-lg: 3.5rem;
    --font-size-md: 2.5rem;
    --font-size-sm: 1.5rem;
  }
}
`
