import React, { useState } from "react";
import * as S from "./index.style";
import Header from "../../components/Header";

import redPikmin from "../../assets/redPikmin.jpeg";

const Explanation = () => {
  const [username, setUsername] = useState("");
  const [newComment, setNewComment] = useState('');
  const [comments, setComments] = useState([]);

  const handleSubmitComment = () => {
    if (newComment.trim() && username.trim()){
      const commentObj = {
        id : Date.now(),
        text : newComment,
        author : username,
        timestamp : new Date().toLocaleString()
      };

      setComments([...comments, commentObj]);
      setNewComment('');
    }
  }

  const handleDeleteComment = (id) => {
    setComments(comments.filter(comment => comment.id !== id));
  }

  return (
    <S.backgroundImg>
      <Header />
      <S.redPikmin src={redPikmin}></S.redPikmin>
      <S.explain>
        <S.explainText>이름 : 빨간 피크민</S.explainText>
        <S.explainText>
          특징 : 피크민에서 처음 등장한 피크민. 특징으로는 색이 붉은색이고
          뾰족한 코와 같은 돌기가 달려 있다.
        </S.explainText>
        <S.explainText>크기 : 29mm</S.explainText>
        <S.explainText>전투력 : 15</S.explainText>
      </S.explain>

      <div>
        <S.Title>댓글</S.Title>
        <S.InputContainer
          type="text"
          placeholder="이름을 입력하세요"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        ></S.InputContainer>

        <div>
          <S.InputContainer
            type="text"
            placeholder="댓글을 입력하세요"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
          ></S.InputContainer>
          <S.StyledButton onClick={handleSubmitComment}>등록</S.StyledButton>
        </div>

        <co>
          {comments.map((comment) => (
            <div key={comment.id}>
              <div>
                <p>{comment.author}</p>
                <p>{comment.text}</p>
                <p>{comment.timestamp}</p>
              </div>
              <buttom variant = 'destructivw' size = 'sm' onClick={() => handleDeleteComment(comment.id)}>
                삭제
              </buttom>
            </div>
          ))}
        </co>
      </div>
    </S.backgroundImg>
  );
};

export default Explanation;
