import styled from "styled-components"
import background from '../../assets/background-img.jpg'
import { createGlobalStyle } from 'styled-components';

export const backgroundImg = styled.div`
    background-image: url(${background});
    background-size: cover;
    background-position: center;
    width: 100%;
    height: 100vh;
    margin: 0;
    padding: 0;
    overflow: hidden;
`

export const redPikmin = styled.img`
    position: absolute;
    left: 5%;
    top: 17%;
    border-radius: 50px;
    width: 13vw;
    height: 40vh;
`

const GlobalStyle = createGlobalStyle`
  @font-face {
    font-family: 'EF_jejudoldam';
    src: url('https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_2210-EF@1.0/EF_jejudoldam.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
  }
`

export const explain = styled.div`
    position: absolute;
    left: 20%;
    top: 18%;
`

export const explainText = styled.div`
    font-size: 25px;
    font-family: 'EF_jejudoldam', sans-serif;
    padding: 5px;
    text-xl       # 모바일 기본 크기
        sm:text-2xl   # 小 스크린 (640px 이상)
        md:text-3xl   # 중간 스크린 (768px 이상)
        lg:text-4xl   # 큰 스크린 (1024px 이상)
        xl:text-5xl  
        font-bold 
        text-center 
        text-blue-700
`
