import styled from "styled-components"
import background from '../../assets/background-img.jpg'

export const backgroundImg = styled.div`
    background-image: url(${background});
    background-size: cover;
    background-position: center;
    width: 100%;
    height: 100vh;
    margin: 0;
    padding: 0;

`
